    <script>
    $(() => clearform())
    function updateForm(Id) {
        fetch('/Home/YazarDetay?Id=' + Id)
            .then(res => res.json())
            .then(res => {
                $('#Id').val(res.id)
                $('#Ad').val(res.ad)
                $('#Soyad').val(res.soyad)
                $('#Email').val(res.email)
                $('#Sifre').val(res.sifre)
            })

        $('#kaydet').hide();
        $('#guncelle').show();
        $('#YazarModal').modal('show');

    }

    function clearform() {


        $('#Id').val(null)
        $('#Ad').val(null)
        $('#Soyad').val(null)
        $('#Email').val(null)
        $('#Sifre').val(null)
        
        $('#kaydet').show();
        $('#guncelle').hide();

    }
    </script>
